findNFish <- function(fish, i = 1, n = 80) {
  new_fish <- sum(fish == 0)
  fish <- fish - 1
  fish[fish == -1] <- 6
  fish <- append(fish, rep(8, new_fish))
  if (i < n) {
    findNFish(fish, i + 1, n)
  } else {
    length(fish)
  }
}

